mapper.select:
SELECT 

FROM goods a

WHERE a.id = #{id}

SELECT
FROM goods a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.cate_no = #{cateNo}
AND a.goods_no LIKE '%'||#{goodsNo}||'%' '%'+#{goodsNo}+'%' concat('%',#{goodsNo},'%')
AND a.goods_name LIKE '%'||#{goodsName}||'%' '%'+#{goodsName}+'%' concat('%',#{goodsName},'%')
AND a.brand_name LIKE '%'||#{brandName}||'%' '%'+#{brandName}+'%' concat('%',#{brandName},'%')
AND a.style LIKE '%'||#{style}||'%' '%'+#{style}+'%' concat('%',#{style},'%')
AND a.material LIKE '%'||#{material}||'%' '%'+#{material}+'%' concat('%',#{material},'%')
AND a.label_type = #{labelType}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.merchant_id = #{merchantId}
AND a.goods_status = #{goodsStatus}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
