mapper.select:
SELECT 

FROM order_log a

WHERE a.id = #{id}

SELECT 

FROM order_log a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_id = #{orderId}




		ORDER BY ${page.orderBy}

SELECT 

FROM order_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE order_log SET 	
	order_id = #{orderId},
	content = #{content}
WHERE id = #{id}

UPDATE order_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
