mapper.select:
SELECT 

FROM order_main a

WHERE a.id = #{id}

SELECT
FROM order_main a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.order_no LIKE '%'||#{orderNo}||'%' '%'+#{orderNo}+'%' concat('%',#{orderNo},'%')
AND a.order_status = #{orderStatus}
AND a.consignee_name LIKE '%'||#{consigneeName}||'%' '%'+#{consigneeName}+'%' concat('%',#{consigneeName},'%')
AND a.consignee_phone LIKE '%'||#{consigneePhone}||'%' '%'+#{consigneePhone}+'%' concat('%',#{consigneePhone},'%')
AND a.freight_number LIKE '%'||#{freightNumber}||'%' '%'+#{freightNumber}+'%' concat('%',#{freightNumber},'%')
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.is_export = #{isExport}
AND a.merchant_id = #{merchantId}
AND a.create_by = #{createBy.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM order_main a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_main SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
