mapper.select:
SELECT 

FROM consignee_addr a

WHERE a.id = #{id}

SELECT
FROM consignee_addr a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.consignee_name LIKE '%'||#{consigneeName}||'%' '%'+#{consigneeName}+'%' concat('%',#{consigneeName},'%')
AND a.mobile_phone LIKE '%'||#{mobilePhone}||'%' '%'+#{mobilePhone}+'%' concat('%',#{mobilePhone},'%')
AND a.merchant_id = #{merchantId}
AND a.user_id = #{user.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM consignee_addr a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE consignee_addr SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
